import React, { useState } from 'react';
import axios from 'axios';

const HorarioUploadComponent = ({ onBack }) => {
    const [horario1, setHorario1] = useState(null);
    const [horario2, setHorario2] = useState(null);

    const handleFileChange1 = (e) => {
        setHorario1(e.target.files[0]);
    };

    const handleFileChange2 = (e) => {
        setHorario2(e.target.files[0]);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!horario1 || !horario2) return;

        const formData = new FormData();
        formData.append('horario1', horario1);
        formData.append('horario2', horario2);

        try {
            await axios.post('/upload-horario', formData);
            alert('Archivos subidos exitosamente');
            setHorario1(null);
            setHorario2(null);
        } catch (error) {
            alert('Error al subir los archivos');
        }
    };

    return (
        <div>
            <h2>Carga Horario</h2>
            <form onSubmit={handleSubmit}>
                <input type="file" onChange={handleFileChange1} />
                <input type="file" onChange={handleFileChange2} />
                <button type="submit">Subir</button>
            </form>
            <button onClick={onBack}>Regresar</button>
        </div>
    );
};

export default HorarioUploadComponent;
