import React, { useEffect, useState } from 'react';
import { AgGridReact } from 'ag-grid-react';
import Modal from 'react-modal';
import '@fortawesome/fontawesome-free/css/all.min.css';
import 'ag-grid-community/styles/ag-grid.css';
import 'ag-grid-community/styles/ag-theme-alpine.css';
import '../style-table.css';

Modal.setAppElement('#root');

const DataTable = () => {
    const [rowData, setRowData] = useState([]);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [activeAction, setActiveAction] = useState('');
    const [rowsToEdit, setRowsToEdit] = useState([]);
    const [modalMessage, setModalMessage] = useState(''); 

    useEffect(() => {
        fetch('http://localhost:5000/api/data')
            .then(response => response.json())
            .then(data => {
                const processedData = processErrors(data); // Procesar errores al cargar
                setRowData(processedData);
            })
            .catch(error => console.error('Error fetching data:', error));
    }, []);

    // Procesar errores en los datos
    const processErrors = (data) => {
        return data.map((row, index, array) => {
            let error = '';

            // Error 1: Omisión de marcaje
            if (
                row['entrada/salida'] === 3 &&
                row['hora_reloj'] === '00:00' &&
                row['Hora Salida'] !== '00:00'
            ) {
                error = '1';
            }

            // Error 2: Marcaje múltiple en 5 minutos
            if (index > 0) {
                const prevRow = array[index - 1];
                const camposAComparar = ['Día', 'RUT', 'entrada/salida', 'fecha_reloj'];
                const todosCamposIguales = camposAComparar.every(campo => row[campo] === prevRow[campo]);
            
                if (todosCamposIguales) {
                    // Extraer horas y minutos de las marcas actuales y previas
                    const [horaActualHoras, horaActualMinutos] = row['hora_reloj'].split(':').map(Number);
                    const [horaAnteriorHoras, horaAnteriorMinutos] = prevRow['hora_reloj'].split(':').map(Number);
            
                    // Crear objetos de fecha solo para comparar tiempos
                    const fechaActual = new Date(0, 0, 0, horaActualHoras, horaActualMinutos);
                    const fechaAnterior = new Date(0, 0, 0, horaAnteriorHoras, horaAnteriorMinutos);
            
                    // Calcular la diferencia en minutos
                    const diferenciaMinutos = Math.abs((fechaActual - fechaAnterior) / 60000);
            
                    // Depuración: Imprimir la diferencia calculada
                    console.log(`Diferencia entre ${prevRow['hora_reloj']} y ${row['hora_reloj']}: ${diferenciaMinutos} minutos`);
            
                    // Solo marcar error si la diferencia es menor o igual a 5 minutos
                    if (diferenciaMinutos <= 5) {
                        error = '2';
                    }
                }
            }
            
            

            // Error 3: Inversión de entrada/salida
            if (index > 0) {
                const prevRow = array.slice(0, index).reverse().find(r => r['RUT'] === row['RUT']);
                if (prevRow) {
                    const esEntradaActual = row['entrada/salida'] === 1;
                    const esSalidaActual = row['entrada/salida'] === 3;
                    const esEntradaPrev = prevRow['entrada/salida'] === 1;
                    const esSalidaPrev = prevRow['entrada/salida'] === 3;

                    if ((esEntradaActual && esEntradaPrev) || (esSalidaActual && esSalidaPrev)) {
                        //error = '3';
                    }
                }
            }

            return { ...row, 'Error encontrado': error };
        });
    };

    const columnDefs = [
        { headerName: 'Día', field: 'Día', flex: 1 },
        { headerName: 'RUT', field: 'RUT', editable: true, flex: 1 },
        { headerName: 'Entrada/Salida', field: 'entrada/salida', editable: true, flex: 1 },
        { headerName: 'Fecha reloj', field: 'fecha_reloj', flex: 1 },
        { headerName: 'Hora reloj', field: 'hora_reloj', editable: true, flex: 1 },
        { headerName: 'Entrada Horario', field: 'Hora Entrada', flex: 1 },
        { headerName: 'Salida Horario', field: 'Hora Salida', editable: true, flex: 1 },
        { headerName: 'Error encontrado', field: 'Error encontrado', flex: 1 },
    ];

    const onCellValueChanged = (params) => {
        const updatedRowData = processErrors([...rowData]);
        setRowData(updatedRowData);
    };

    const handleOmission = () => {
        const affectedRows = rowData.filter((row) => row['Error encontrado'] === '1');
        affectedRows.forEach((row) => {
            row.hora_reloj = row['Hora Salida'];
        });
        setRowsToEdit(affectedRows);
        setModalMessage('BLABLABAL'); // Mensaje específico
        setIsModalOpen(true);
    };
    
    const handleMultipleMarks = () => {
        const affectedRows = rowData.filter((row) => row['Error encontrado'] === '2');
        setRowsToEdit(affectedRows);
        setIsModalOpen(true);
    };
    
    const handleIncorrectEntryExit = () => {
        const affectedRows = rowData.filter((row) => row['Error encontrado'] === '3');
        affectedRows.forEach((row) => {
            row['entrada/salida'] = row['entrada/salida'] === '1' ? '3' : '1';
        });
        setRowsToEdit(affectedRows);
        setIsModalOpen(true);
    };
    
    const applyChanges = () => {
        // Actualiza el estado de rowData después de confirmar
        const newData = [...rowData];
        rowsToEdit.forEach((editedRow) => {
            const index = newData.findIndex((row) => row['RUT'] === editedRow['RUT'] && row['Día'] === editedRow['Día']);
            if (index !== -1) newData[index] = editedRow;
        });
        setRowData(newData);
        setIsModalOpen(false);
        setRowsToEdit([]);
    };
    

    return (
        <><div style={{ display: 'flex', alignItems: 'center', width: '65%' }}>
            {/* Caso 1: Omisión de Marcaje */}
            <div style={{ flex: 1 }}>
                <h3>Omisión de Marcaje</h3>
                <p>
                    Permite que en aquellas filas donde la entrada no es marcada se le asigne el horario programado.
                </p>
            </div>
            <div style={{ marginLeft: '5px', display: 'flex', justifyContent: 'center', flex: 0 }}>
                <button
                    onClick={handleOmission}
                    style={{
                        padding: '10px 20px',
                        backgroundColor: '#007bff',
                        color: 'white',
                        border: 'none',
                        borderRadius: '5px',
                        minWidth: '200px',
                        cursor: 'pointer',
                    }}
                >
                    Modificar
                </button>
            </div>
        </div><div style={{ display: 'flex', alignItems: 'center', width: '65%' }}>
                {/* Caso 2: Marcaje Múltiple */}
                <div style={{ flex: 1 }}>
                    <h3>Marcaje múltiple</h3>
                    <p>
                        Encuentra filas en donde se ha marcado más de 1 vez, en un rango de tiempo de 5 minutos.
                    </p>
                </div>
                <div style={{ marginLeft: '5px', display: 'flex', justifyContent: 'center', flex: 0 }}>
                    <button
                        onClick={handleMultipleMarks}
                        style={{
                            padding: '10px 20px',
                            backgroundColor: '#007bff',
                            color: 'white',
                            border: 'none',
                            borderRadius: '5px',
                            minWidth: '200px',
                            cursor: 'pointer',
                        }}
                    >
                        Modificar
                    </button>
                </div>
            </div><div style={{ display: 'flex', alignItems: 'center', width: '65%' }}>
                {/* Caso 3: Entrada/Salida Incorrecta */}
                <div style={{ flex: 1 }}>
                    <h3>Ajustar Entrada/Salida Incorrecta</h3>
                    <p>
                        Revisa que no existan dos entradas o dos salidas consecutivas para la misma persona.
                    </p>
                </div>
                <div style={{ marginLeft: '5px', display: 'flex', justifyContent: 'center', flex: 0 }}>
                    <button
                        onClick={handleIncorrectEntryExit}
                        style={{
                            padding: '10px 20px',
                            backgroundColor: '#007bff',
                            color: 'white',
                            border: 'none',
                            borderRadius: '5px',
                            minWidth: '200px',
                            cursor: 'pointer',
                        }}
                    >
                        Modificar
                    </button>
                </div>
            </div>
            <><Modal
                isOpen={isModalOpen}
                onRequestClose={() => setIsModalOpen(false)}
                contentLabel="Confirmación de Cambios"
                style={{
                    content: {
                        maxWidth: '500px',
                        margin: 'auto',
                        padding: '20px',
                        borderRadius: '8px',
                        border: '1px solid #ccc',
                    },
                }}
            >
                <h2 style={{ textAlign: 'center' }}>Confirmación de Cambios</h2>
                <p>Las siguientes filas serán editadas:</p>
                <ul style={{ paddingLeft: '20px' }}>
                    {rowsToEdit.map((row, index) => (
                        <li key={index}>
                            Día: {row['Día']}, RUT: {row['RUT']}, Hora Reloj: {row['hora_reloj']}, Hora Salida Horario: {row['Hora Salida']}
                        </li>
                    ))}
                </ul>
                <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
                    <button
                        onClick={applyChanges}
                        style={{
                            padding: '10px 20px',
                            marginRight: '10px',
                            backgroundColor: '#28a745',
                            color: 'white',
                            border: 'none',
                            borderRadius: '5px',
                            cursor: 'pointer',
                        }}
                    >
                        Confirmar
                    </button>
                    <button
                        onClick={() => setIsModalOpen(false)}
                        style={{
                            padding: '10px 20px',
                            backgroundColor: '#dc3545',
                            color: 'white',
                            border: 'none',
                            borderRadius: '5px',
                            cursor: 'pointer',
                        }}
                    >
                        Cancelar
                    </button>
                </div>
            </Modal><>
                    <div className="ag-theme-alpine" style={{ height: '600px', width: '100%' }}>
                        <AgGridReact
                            rowData={rowData}
                            columnDefs={columnDefs}
                            rowClassRules={{
                                'row-red': params => params.data['Error encontrado'] === '1',
                                'row-yellow': params => params.data['Error encontrado'] === '2',
                                //'row-orange': params => params.data['Error encontrado'] === '3',
                            }}
                            defaultColDef={{ editable: true, flex: 1 }}
                            onCellValueChanged={onCellValueChanged} />
                    </div>
                </></></>
    );
};

export default DataTable;
