import React, { useState } from 'react';
import { useDropzone } from 'react-dropzone';
import fileIcon from '../file-icon.png'; // Asegúrate de que esta ruta es correcta
import './styles.css'; // Asegúrate de que esta ruta es correcta

const UploadComponent = () => {
    const [file, setFile] = useState(null);
    const [message, setMessage] = useState('');

    const onDrop = (acceptedFiles) => {
        const selectedFile = acceptedFiles[0];
        setFile(selectedFile);
        uploadFile(selectedFile);
    };

    const uploadFile = (file) => {
        const formData = new FormData();
        formData.append('file', file);

        fetch('http://localhost:5000/upload-reloj', { // Cambia la URL según tu configuración
            method: 'POST',
            body: formData,
        })
        .then(response => response.json())
        .then(data => {
            if (data.error) {
                setMessage(`Error: ${data.error}`);
            } else {
                setMessage(`Archivo ${file.name} subido exitosamente!`);
            }
        })
        .catch(error => {
            console.error('Error al subir el archivo:', error);
            setMessage('Error al subir el archivo. Inténtalo de nuevo.');
        });
    };

    const { getRootProps, getInputProps } = useDropzone({ onDrop });

    return (
        <div>
            <h2>Carga Reloj</h2>
            <div className="upload-area" {...getRootProps()}>
                <input {...getInputProps()} />
                <img src={fileIcon} alt="File icon" />
                {file ? <p>Archivo seleccionado: {file.name}</p> : <p>Arrastra y suelta un archivo aquí, o haz clic para seleccionar uno</p>}
            </div>
            {message && <p>{message}</p>}
        </div>
    );
};

export default UploadComponent;
