import React from 'react';
import { useDropzone } from 'react-dropzone';
import fileIcon from '../public/file-icon.png'; // Asegúrate de que la ruta sea correcta

const UploadComponent = () => {
    const [relojFile, setRelojFile] = React.useState(null);
    const [message, setMessage] = React.useState('');

    const onDrop = (acceptedFiles) => {
        const selectedFile = acceptedFiles[0];
        setRelojFile(selectedFile);
        uploadFile(selectedFile);
    };

    const uploadFile = (file) => {
        const formData = new FormData();
        formData.append('reloj', file);

        fetch('http://localhost:5000/upload-reloj', { // Cambia la URL según tu configuración
            method: 'POST',
            body: formData,
        })
        .then(response => response.json())
        .then(data => {
            if (data.error) {
                setMessage(`Error: ${data.error}`);
            } else {
                setMessage(`Archivo subido exitosamente!`);
            }
        })
        .catch(error => {
            console.error('Error al subir el archivo:', error);
            setMessage('Error al subir el archivo. Inténtalo de nuevo.');
        });
    };

    const { getRootProps, getInputProps } = useDropzone({ onDrop });

    return (
        <div className="upload-container">
            <h2>Carga Reloj</h2>
            <div className="upload-area" {...getRootProps()}>
                <input {...getInputProps()} />
                <img src={`${process.env.PUBLIC_URL}/file-icon.png`} alt="File icon" />
                {relojFile ? <p>Archivo seleccionado: {relojFile.name}</p> : <p>Arrastra y suelta el archivo aquí, o haz clic para seleccionar uno</p>}
            </div>
            {message && <p>{message}</p>}
        </div>
    );
};

export default UploadComponent;
