import React, { useEffect, useState } from 'react';
import { AgGridReact } from 'ag-grid-react';
import Modal from 'react-modal';
import '@fortawesome/fontawesome-free/css/all.min.css';
import 'ag-grid-community/styles/ag-grid.css';
import 'ag-grid-community/styles/ag-theme-alpine.css';
import 'font-awesome/css/font-awesome.min.css';
import '../style-table.css';

Modal.setAppElement('#root');

const DataTable = () => {
    const [rowData, setRowData] = useState([]);
    const [rowsToEdit, setRowsToEdit] = useState([]);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [activeAction, setActiveAction] = useState('');

    // Cargar datos desde el servidor
    useEffect(() => {
        fetch('http://localhost:5000/api/data')
            .then(response => response.json())
            .then(data => setRowData(data))
            .catch(error => console.error('Error fetching data:', error));
    }, []);

    const columnDefs = [
        { headerName: 'Día', field: 'Día', flex: 1 },
        { headerName: 'RUT', field: 'RUT', editable: true, flex: 1 },
        { headerName: 'Entrada/Salida', field: 'entrada/salida', editable: true, flex: 1 },
        { headerName: 'Fecha reloj', field: 'fecha_reloj', flex: 1 },
        { headerName: 'Hora reloj', field: 'hora_reloj', editable: true, flex: 1 },
        { headerName: 'Entrada Horario', field: 'Hora Entrada', flex: 1 },
        { headerName: 'Salida Horario', field: 'Hora Salida', editable: true, flex: 1 },
        { headerName: 'Error encontrado', field: 'Error encontrado', flex: 1 },
    ];

    const gridOptions = {
        columnDefs: columnDefs,
        defaultColDef: {
            resizable: false,
            sortable: false,
        },
    };

    // Reglas de clase sin modificar el estado
    const rowClassRules = {
        'row-red': params =>
            params.data['entrada/salida'] === 3 &&
            params.data['hora_reloj'] === '00:00' &&
            params.data['Hora Salida'] !== '00:00',
        'row-yellow': params => {
            const filaActual = params.data;
            if (params.node.rowIndex === 0) return false;

            const filaAnterior = params.api.getDisplayedRowAtIndex(params.node.rowIndex - 1);
            if (!filaAnterior || !filaAnterior.data) return false;

            const camposAComparar = ['Día', 'RUT', 'entrada/salida', 'fecha_reloj'];
            const todosCamposIguales = camposAComparar.every(campo => filaActual[campo] === filaAnterior.data[campo]);

            if (!todosCamposIguales) return false;

            const [horaActualHoras, horaActualMinutos] = filaActual['hora_reloj'].split(':').map(Number);
            const [horaAnteriorHoras, horaAnteriorMinutos] = filaAnterior.data['hora_reloj'].split(':').map(Number);

            const fechaActual = new Date(0, 0, 0, horaActualHoras, horaActualMinutos);
            const fechaAnterior = new Date(0, 0, 0, horaAnteriorHoras, horaAnteriorMinutos);

            const diferenciaMinutos = Math.abs((fechaActual - fechaAnterior) / 60000);
            return diferenciaMinutos <= 5;
        },
        'row-orange': params => {
            const filaActual = params.data;
            const rutActual = filaActual['RUT'];
            const filasPrevias = [];
            params.api.forEachNode((node, index) => {
                if (index < params.node.rowIndex && node.data['RUT'] === rutActual) {
                    filasPrevias.push(node.data);
                }
            });

            if (filasPrevias.length === 0) return false;

            const ultimoMarcaje = filasPrevias[filasPrevias.length - 1];
            const esEntradaActual = filaActual['entrada/salida'] === 1;
            const esSalidaActual = filaActual['entrada/salida'] === 3;
            const esEntradaUltima = ultimoMarcaje['entrada/salida'] === 1;
            const esSalidaUltima = ultimoMarcaje['entrada/salida'] === 3;

            return (esEntradaActual && esEntradaUltima) || (esSalidaActual && esSalidaUltima);
        },
    };

    // Abrir modal para modificar filas
    const prepareChanges = () => {
        const filteredRows = rowData.filter(
            row =>
                row['entrada/salida'] === 3 &&
                row['hora_reloj'] === '00:00' &&
                row['Hora Salida'] !== '00:00'
        );
        setRowsToEdit(filteredRows);
        setActiveAction('edit');
        setIsModalOpen(true);
    };

    // Aplicar cambios
    const applyChanges = () => {
        if (activeAction === 'edit') {
            const updatedRowData = rowData.map(row =>
                rowsToEdit.includes(row)
                    ? { ...row, hora_reloj: row['Hora Salida'], 'Error encontrado': '' }
                    : row
            );
            setRowData(updatedRowData);
        }
        setIsModalOpen(false);
    };

    return (
        <>
        <i className="fas fa-exclamation-triangle" style={{ color: 'red' }}></i>
            <div style={{ display: 'flex', alignItems: 'center', width: '65%' }}>
                <div style={{ flex: 1 }}>
                    <h3>1-. Omisión de Marcaje</h3>
                    <p>
                        Permite que en aquellas filas donde la entrada no es marcada se le asigne el horario programado.
                    </p>
                </div>
                <div style={{ marginLeft: '5px', display: 'flex', justifyContent: 'center', flex: 0 }}>
                    <button
                        onClick={prepareChanges}
                        style={{
                            padding: '10px 20px',
                            backgroundColor: '#007bff',
                            color: 'white',
                            border: 'none',
                            borderRadius: '5px',
                            minWidth: '200px',
                            cursor: 'pointer',
                        }}
                    >
                        Modificar
                    </button>
                </div>
            </div>
            <div style={{ display: 'flex', alignItems: 'center', width: '65%' }}>
                <div style={{ flex: 1 }}>
                    <h3>2-. Marcaje múltiple</h3>
                    <p>
                        Encuentra filas en donde se ha marcado mas de 1 vez, en un rango de tiempo de 5 minutos
                    </p>
                </div>
                <div style={{ marginLeft: '5px', display: 'flex', justifyContent: 'center', flex: 0 }}>
                    <button
                        onClick={prepareDeletions}
                        style={{
                            padding: '10px 20px',
                            backgroundColor: '#007bff',
                            color: 'white',
                            border: 'none',
                            borderRadius: '5px',
                            minWidth: '200px',
                            cursor: 'pointer',
                        }}
                    >
                        Modificar
                    </button>
                </div>
            </div>
            <div style={{ display: 'flex', alignItems: 'center', width: '65%' }}>
                <div style={{ flex: 1 }}>
                    <h3>3-. Ajustar Entrada/Salida Incorrecta</h3>
                    <p>
                        Revisa que no existan dos entradas o dos salidas consecutivas para la misma persona.
                    </p>
                </div>
                <div style={{ marginLeft: '5px', display: 'flex', justifyContent: 'center', flex: 0 }}>
                    <button
                        onClick={prepareChanges}
                        style={{
                            padding: '10px 20px',
                            backgroundColor: '#007bff',
                            color: 'white',
                            border: 'none',
                            borderRadius: '5px',
                            minWidth: '200px',
                            cursor: 'pointer',
                        }}
                    >
                        Modificar
                    </button>
                </div>
            </div>
                
            <Modal
                isOpen={isModalOpen}
                onRequestClose={() => setIsModalOpen(false)}
                contentLabel="Confirmación de Cambios"
                style={{
                    content: {
                        maxWidth: '500px',
                        margin: 'auto',
                        padding: '20px',
                        borderRadius: '8px',
                        border: '1px solid #ccc',
                    }
                }}
            >
                <h2 style={{ textAlign: 'center' }}>
                    {activeAction === 'edit' ? 'Confirmar Edición' : 'Confirmar Eliminación'}
                </h2>
                <p>
                    {activeAction === 'edit'
                        ? 'Las siguientes filas serán editadas:'
                        : 'Las siguientes filas serán eliminadas:'}
                </p>
                <ul style={{ paddingLeft: '20px' }}>
                    {rowsToEdit.map((row, index) => (
                        <li key={index}>
                            Día: {row['Día']}, RUT: {row['RUT']}, Hora Reloj: {row['hora_reloj']}
                            {activeAction === 'edit' && (
                            <>
                            <br />
                            <strong>Nuevos valores:</strong> 
                            <span style={{ color: '#28a745' }}>
                                Día: {row.newDay || row['Día']}, Nueva Hora Reloj: {row.newHour || row['Hora Salida']}
                            </span>
                            </>
                    )}
                        </li>
                    ))}
                </ul>
                <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
                    <button
                        onClick={applyChanges}
                        style={{
                            padding: '10px 20px',
                            marginRight: '10px',
                            backgroundColor: '#28a745',
                            color: 'white',
                            border: 'none',
                            borderRadius: '5px',
                            cursor: 'pointer',
                        }}
                    >
                        Confirmar
                    </button>
                    <button
                        onClick={() => setIsModalOpen(false)}
                        style={{
                            padding: '10px 20px',
                            backgroundColor: '#dc3545',
                            color: 'white',
                            border: 'none',
                            borderRadius: '5px',
                            cursor: 'pointer',
                        }}
                    >
                        Cancelar
                    </button>
                </div>
            </Modal>


            <div className="ag-theme-alpine" style={{ height: '400px', width: '100%' }}>
                <AgGridReact
                    rowData={rowData}
                    gridOptions={gridOptions}
                    columnDefs={columnDefs}
                    rowClassRules={rowClassRules}
                    pagination={true}
                    sorting={true}
                    paginationPageSize={10} // Número de filas por página
                />
            </div>
        </>
    );
};

export default DataTable;
