import React, { useState } from 'react';
import axios from 'axios';

const HorarioUploadComponent = () => {
    const [horario1, setHorario1] = useState(null);
    const [horario2, setHorario2] = useState(null);
    const [showConfirm, setShowConfirm] = useState(false);
    const [message, setMessage] = useState('');

    const handleFileChange = (e, setFile) => {
        setFile(e.target.files[0]);
    };

    const handleUpload = () => {
        if (horario1 && horario2) {
            setShowConfirm(true);
        }
    };

    const confirmUpload = () => {
        const formData = new FormData();
        formData.append('horario1', horario1);
        formData.append('horario2', horario2);

        axios.post('http://localhost:5000/upload-horario', formData)
            .then(response => {
                setMessage(response.data.message);
                setShowConfirm(false);
                setHorario1(null);
                setHorario2(null);
            })
            .catch(error => {
                setMessage(error.response.data.error || 'Error al subir los archivos');
                setShowConfirm(false);
            });
    };

    return (
        <div>
            <h1>Subir Archivos - Carga Horario</h1>
            <input type="file" onChange={(e) => handleFileChange(e, setHorario1)} />
            <input type="file" onChange={(e) => handleFileChange(e, setHorario2)} />
            <button onClick={handleUpload}>Subir</button>
            {showConfirm && (
                <div>
                    <p>¿Está seguro de que desea subir los archivos?</p>
                    <button onClick={confirmUpload}>Confirmar</button>
                    <button onClick={() => setShowConfirm(false)}>Cancelar</button>
                </div>
            )}
            {message && <p>{message}</p>}
        </div>
    );
};

export default HorarioUploadComponent;
