import React from 'react';
import { useDropzone } from 'react-dropzone';
import fileIcon from '../file-icon.png';


const UploadComponent = () => {
    const [file, setFile] = useState(null);
    const [message, setMessage] = useState('');

    const onDrop = (acceptedFiles) => {
        const selectedFile = acceptedFiles[0];
        setFile(selectedFile);
        uploadFile(selectedFile);
    };

    const uploadFile = (file) => {
        const formData = new FormData();
        formData.append('file', file);

        fetch('http://localhost:5000/upload-reloj', { // Cambia la URL según tu configuración
            method: 'POST',
            body: formData,
        })
        .then(response => response.json())
        .then(data => {
            if (data.error) {
                setMessage(`Error: ${data.error}`);
            } else {
                setMessage(`Archivo ${file.name} subido exitosamente!`);
            }
        })
        .catch(error => {
            console.error('Error al subir el archivo:', error);
            setMessage('Error al subir el archivo. Inténtalo de nuevo.');
        });
    };

    const { getRootProps, getInputProps } = useDropzone({ onDrop });

    return (
        <div className="upload-container">
          <h2>Carga Reloj</h2>
          <div {...getRootProps({ className: 'dropzone' })}>
            <input {...getInputProps()} />
            <p>Arrastra y suelta un archivo aquí, o haz clic para seleccionar uno</p>
            <img src={fileIcon} alt="File Icon" className="file-icon" />
          </div>
        </div>
      );
    };
    
    export default UploadComponent;
