import React from 'react';
import { useDropzone } from 'react-dropzone';
import fileIcon from '../file-icon.png';

const UploadComponent = () => {
    let archivoReloj = null;
    let message = '';

    const onDrop = (acceptedFiles) => {
        const selectedFile = acceptedFiles[0];
        archivoReloj = selectedFile;
        uploadFile(selectedFile);
    };

    const uploadFile = (file) => {
        const formData = new FormData();
        formData.append('reloj', file);

        fetch('http://localhost:5000/upload-reloj', { // Cambia la URL según tu configuración
            method: 'POST',
            body: formData,
        })
        .then(response => response.json())
        .then(data => {
            if (data.error) {
                message = `Error: ${data.error}`;
            } else {
                message = `Archivo subido exitosamente!`;
            }
            alert(message); // Muestra el mensaje
        })
        .catch(error => {
            console.error('Error al subir el archivo:', error);
            message = 'Error al subir el archivo. Inténtalo de nuevo.';
            alert(message); // Muestra el mensaje
        });
    };

    const { getRootProps, getInputProps } = useDropzone({ onDrop });

    return (
        <div>
            <h2>Carga Reloj</h2>
            <div className="upload-area" {...getRootProps()}>
                <input {...getInputProps()} />
                <img src={`${process.env.PUBLIC_URL}/file-icon.png`} alt="File icon" />
                {archivoReloj ? <p>Archivo reloj seleccionado: {archivoReloj.name}</p> : <p>Arrastra y suelta el archivo reloj aquí, o haz clic para seleccionar uno</p>}
            </div>
        </div>
    );
};

export default UploadComponent;
