import React, { useState } from 'react';
import axios from 'axios';
import fileIcon from '../file-icon.png'; // Asegúrate de que la ruta sea correcta

const UploadComponent = () => {
    const [file, setFile] = useState(null);

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    const handleDragOver = (e) => {
        e.preventDefault();
    };

    const handleDrop = (e) => {
        e.preventDefault();
        const droppedFile = e.dataTransfer.files[0];
        setFile(droppedFile);
    };

    const handleUpload = async () => {
        if (!file) return;

        const formData = new FormData();
        formData.append('file', file);

        try {
            const response = await axios.post('http://localhost:5000/upload-reloj', formData);
            alert(response.data.message);
            setFile(null); // Reiniciar el estado
        } catch (error) {
            alert(error.response.data.error);
        }
    };

    return (
        <div className="upload-container">
            <h2>Carga Reloj</h2>
            <div
                className="drop-area"
                onDragOver={handleDragOver}
                onDrop={handleDrop}
                onClick={() => document.getElementById('fileInput').click()}
            >
                {file ? (
                    <div className="file-info">
                        <img src={fileIcon} alt="File icon" />
                        <p>{file.name}</p>
                    </div>
                ) : (
                    <p>Arrastra un archivo aquí o haz clic para seleccionar uno</p>
                )}
                <input
                    id="fileInput"
                    type="file"
                    onChange={handleFileChange}
                    style={{ display: 'none' }}
                />
            </div>
            <button onClick={handleUpload}>Subir</button>
        </div>
    );
};

export default UploadComponent;
