import React, { useEffect, useState } from 'react';
import { useReactTable } from '@tanstack/react-table';

const Table = () => {
    const [data, setData] = useState([]);

    // Fetch data desde el backend
    useEffect(() => {
        fetch('http://localhost:5000/api/data')
            .then(response => response.json())
            .then(data => setData(data))
            .catch(error => console.error('Error fetching data:', error));
    }, []);

    // Define las columnas
    const columns = React.useMemo(
        () => [
            { header: 'Día', accessorKey: 'Día' },
            { header: 'Hora Entrada', accessorKey: 'Hora Entrada' },
            { header: 'Hora Salida', accessorKey: 'Hora Salida' },
            { header: 'Código Horario', accessorKey: 'Código Horario' },
            { header: 'entrada/salida', accessorKey: 'entrada/salida' },
            { header: 'hora_reloj', accessorKey: 'hora_reloj' },
            { header: 'fecha_reloj', accessorKey: 'fecha_reloj' },
        ],
        []
    );

    // Configura la tabla con useReactTable
    const table = useReactTable({
        data,
        columns,
    });

    return (
        <table style={{ border: 'solid 1px black' }}>
            <thead>
                {table.getHeaderGroups().map(headerGroup => (
                    <tr key={headerGroup.id}>
                        {headerGroup.headers.map(header => (
                            <th key={header.id} style={{ border: 'solid 1px gray' }}>
                                {header.isPlaceholder ? null : header.render('Header')}
                            </th>
                        ))}
                    </tr>
                ))}
            </thead>
            <tbody>
                {table.getRowModel().rows.map(row => (
                    <tr key={row.id}>
                        {row.getVisibleCells().map(cell => (
                            <td key={cell.id} style={{ border: 'solid 1px gray' }}>
                                {cell.render('Cell')}
                            </td>
                        ))}
                    </tr>
                ))}
            </tbody>
        </table>
    );
};

export default Table;
