import React, { useState } from 'react';
import axios from 'axios';
import fileIcon from '../file-icon.png'; // Asegúrate de que la ruta sea correcta

const HorarioUploadComponent = () => {
    const [horario1, setHorario1] = useState(null);
    const [horario2, setHorario2] = useState(null);

    const handleFileChange = (e, setHorario) => {
        setHorario(e.target.files[0]);
    };

    const handleDragOver = (e) => {
        e.preventDefault();
    };

    const handleDrop = (e, setHorario) => {
        e.preventDefault();
        const droppedFile = e.dataTransfer.files[0];
        setHorario(droppedFile);
    };

    const handleUpload = async () => {
        if (!horario1 || !horario2) return;

        const formData = new FormData();
        formData.append('horario1', horario1);
        formData.append('horario2', horario2);

        try {
            const response = await axios.post('http://localhost:5000/upload-horario', formData);
            alert(response.data.message);
            setHorario1(null);
            setHorario2(null);
        } catch (error) {
            alert(error.response.data.error);
        }
    };

    return (
        <div className="upload-container">
            <h2>Carga Horario</h2>
            <div
                className="drop-area"
                onDragOver={handleDragOver}
                onDrop={(e) => handleDrop(e, setHorario1)}
                onClick={() => document.getElementById('horario1Input').click()}
            >
                {horario1 ? (
                    <div className="file-info">
                        <img src={fileIcon} alt="File icon" />
                        <p>{horario1.name}</p>
                    </div>
                ) : (
                    <p>Arrastra el archivo Horario 1 aquí o haz clic para seleccionar uno</p>
                )}
                <input
                    id="horario1Input"
                    type="file"
                    onChange={(e) => handleFileChange(e, setHorario1)}
                    style={{ display: 'none' }}
                />
            </div>

            <div
                className="drop-area"
                onDragOver={handleDragOver}
                onDrop={(e) => handleDrop(e, setHorario2)}
                onClick={() => document.getElementById('horario2Input').click()}
            >
                {horario2 ? (
                    <div className="file-info">
                        <img src={fileIcon} alt="File icon" />
                        <p>{horario2.name}</p>
                    </div>
                ) : (
                    <p>Arrastra el archivo Horario 2 aquí o haz clic para seleccionar uno</p>
                )}
                <input
                    id="horario2Input"
                    type="file"
                    onChange={(e) => handleFileChange(e, setHorario2)}
                    style={{ display: 'none' }}
                />
            </div>

            <button onClick={handleUpload}>Subir</button>
        </div>
    );
};

export default HorarioUploadComponent;
