import React from 'react';
import { useDropzone } from 'react-dropzone';

const HorarioUploadComponent = () => {
    let horario1 = null;
    let horario2 = null;
    let message = '';

    const onDropHorario1 = (acceptedFiles) => {
        const selectedFile = acceptedFiles[0];
        horario1 = selectedFile;
        uploadFile(selectedFile, 'horario1');
    };

    const onDropHorario2 = (acceptedFiles) => {
        const selectedFile = acceptedFiles[0];
        horario2 = selectedFile;
        uploadFile(selectedFile, 'horario2');
    };

    const uploadFile = (file, tipo) => {
        const formData = new FormData();
        formData.append(tipo, file);

        fetch('http://localhost:5000/upload-horario', { // Cambia la URL según tu configuración
            method: 'POST',
            body: formData,
        })
        .then(response => response.json())
        .then(data => {
            if (data.error) {
                message = `Error: ${data.error}`;
            } else {
                message = `Archivos subidos exitosamente!`;
            }
            alert(message); // Muestra el mensaje
        })
        .catch(error => {
            console.error('Error al subir el archivo:', error);
            message = 'Error al subir el archivo. Inténtalo de nuevo.';
            alert(message); // Muestra el mensaje
        });
    };

    const { getRootProps: getRootPropsHorario1, getInputProps: getInputPropsHorario1 } = useDropzone({ onDrop: onDropHorario1 });
    const { getRootProps: getRootPropsHorario2, getInputProps: getInputPropsHorario2 } = useDropzone({ onDrop: onDropHorario2 });

    return (
        <div>
            <h2>Carga Horarios</h2>
            <div className="upload-area" {...getRootPropsHorario1()}>
                <input {...getInputPropsHorario1()} />
                <img src={`${process.env.PUBLIC_URL}/file-icon.png`} alt="File icon" />
                {horario1 ? <p>Archivo horario 1 seleccionado: {horario1.name}</p> : <p>Arrastra y suelta el archivo horario 1 aquí, o haz clic para seleccionar uno</p>}
            </div>
            <div className="upload-area" {...getRootPropsHorario2()}>
                <input {...getInputPropsHorario2()} />
                <img src={`${process.env.PUBLIC_URL}/file-icon.png`} alt="File icon" />
                {horario2 ? <p>Archivo horario 2 seleccionado: {horario2.name}</p> : <p>Arrastra y suelta el archivo horario 2 aquí, o haz clic para seleccionar uno</p>}
            </div>
        </div>
    );
};

export default HorarioUploadComponent;
