import React, { useState } from 'react';
import axios from 'axios';

const UploadComponent = () => {
    const [file, setFile] = useState(null);
    const [showConfirm, setShowConfirm] = useState(false);
    const [message, setMessage] = useState('');

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    const handleUpload = () => {
        if (file) {
            setShowConfirm(true);
        }
    };

    const confirmUpload = () => {
        const formData = new FormData();
        formData.append('file', file);

        axios.post('http://localhost:5000/upload-reloj', formData)
            .then(response => {
                setMessage(response.data.message);
                setShowConfirm(false);
                setFile(null);
            })
            .catch(error => {
                setMessage(error.response.data.error || 'Error al subir el archivo');
                setShowConfirm(false);
            });
    };

    return (
        <div>
            <h1>Subir Archivo - Carga Reloj</h1>
            <input type="file" onChange={handleFileChange} />
            <button onClick={handleUpload}>Subir</button>
            {showConfirm && (
                <div>
                    <p>¿Está seguro de que desea subir el archivo {file.name}?</p>
                    <button onClick={confirmUpload}>Confirmar</button>
                    <button onClick={() => setShowConfirm(false)}>Cancelar</button>
                </div>
            )}
            {message && <p>{message}</p>}
        </div>
    );
};

export default UploadComponent;
