    const ultimoMarcaje = filasPrevias[filasPrevias.length - 1];

    // Validar el tipo de entrada/salida
    const esEntradaActual = filaActual['entrada/salida'] === 1; // Actual es "entrada"
    const esSalidaActual = filaActual['entrada/salida'] === 3; // Actual es "salida"
    const esEntradaUltima = ultimoMarcaje['entrada/salida'] === 1; // Última es "entrada"
    const esSalidaUltima = ultimoMarcaje['entrada/salida'] === 3; // Última es "salida"

    // Detectar error de marcaje
    const errorInversionEntrada = esEntradaActual && esEntradaUltima; // Dos entradas seguidas
    const errorInversionSalida = esSalidaActual && esSalidaUltima;   // Dos salidas seguidas

    // Si hay un error de inversión de entrada/salida
    if (errorInversionEntrada || errorInversionSalida) {
        setActiveAction(true);
        setIsModalOpen(true);
    }

};

    // Preparar las filas a modificar y abrir el modal
    const prepareChanges = () => {
        const filteredRows = rowData.filter(row =>
            row['entrada/salida'] === 3 &&
            row['hora_reloj'] === "00:00" &&
            row['Hora Salida'] !== "00:00"
        );
